import React, { useState } from "react";
import "./style.css";
import { AiOutlineMail } from "react-icons/ai";
import { MdLockOutline } from "react-icons/md";
import { AiFillEye } from "react-icons/ai";
import Rules from "../../utils/Rules";
import { MdOutlineCancel } from "react-icons/md";
import { TiTick } from "react-icons/ti";
import { ReactComponent as TickIcon } from "../../svg/tickIcon.svg";
import translate from "../../localization/translate";

const Signup = (props) => {
  const {
    onSubmit,
    SignupForm,
    onChange,
    passwordRules,
    PasswordPolicyState,
    onClick,
    isValid,
    SignupError,
  } = props;
  const [showPassword, setShowPassword] = useState(false);
  const [displayRules, setDisplayRules] = useState(false);
  const ruleMap =
    passwordRules &&
    Rules({
      count: passwordRules?.password_complexity_options?.min_length,
    });

  const displayablerule = [];
  const getKeys = [];
  console.log("rules", passwordRules, ruleMap);
  for (const key of Object.keys(PasswordPolicyState)) {
    if (
      key == "Non_empty_Password_Required" ||
      key == "Lower_case_Upper_Case_Numbers" ||
      key == "Length_Check"
    ) {
      getKeys.push(key);
      displayablerule.push(ruleMap[key]);
    }
  }
  console.log("rulesss", displayablerule);
  //   } else if (passwordRules?.passwordPolicy === "low") {
  //     for (const key of Object.keys(PasswordPolicyState)) {
  //       if (
  //         key !== "No_more_than_2_identical_characters_in_a_row" ||
  //         key !== "Special_characters" ||
  //         key !== "Lower_case_Upper_Case_Numbers"
  //       ) {
  //         getKeys.push(key);
  //         displayablerule.push(ruleMap[key]);
  //       }
  //     }
  //   } else if (passwordRules?.passwordPolicy === null) {
  //     for (const key of Object.keys(PasswordPolicyState)) {
  //       if (key === "Non_empty_Password_Required") {
  //         getKeys.push(key);
  //         displayablerule.push(ruleMap[key]);
  //       }
  //     }
  //   }
  return (
    <div className="formWrapper">
      <form className="InputWrapper">
        <>
          {SignupForm.email !== "" ? (
            <div className="InputLabel">{translate("emailAddress")}</div>
          ) : null}
          <div
            className="InputAndLogoSignup"
            // style={{
            //   border:
            //     LoginError.isEmailError === true
            //       ? "2px solid red"
            //       : LoginError.isEmailError === false
            //       ? "2px solid green"
            //       : "",
            // }}
          >
            <AiOutlineMail
              style={{
                height: "2rem",
                width: "2rem",
                marginTop: "0.5rem",
                color: "rgb(175, 174, 174)",
              }}
            />
            <input
              type="email"
              id="email"
              name="email"
              value={SignupForm.email}
              placeholder="Email"
              className="Input"
              onChange={onChange}
            />
          </div>
          <div>
            {SignupForm.password !== "" ? (
              <div
                className="InputLabelPass"
                style={{
                  color: isValid ? "#0CA77D" : "rgb(175, 174, 174)",
                }}
              >
                {translate("password")}
              </div>
            ) : null}
            <div
              className="InputAndLogoSignup"
              // style={{
              //   border:
              //     LoginError.isEmailError === true
              //       ? "2px solid red"
              //       : LoginError.isEmailError === false
              //       ? "2px solid green"
              //       : "",
              // }}
              style={{
                border: `1px solid ${
                  isValid ? "#0CA77D" : "RGB(212, 213, 219)"
                }`,
              }}
            >
              <MdLockOutline
                style={{
                  height: "2rem",
                  width: "2rem",
                  marginTop: "0.5rem",
                  color: "rgb(175, 174, 174)",
                }}
              />
              <input
                type={showPassword ? "text" : "password"}
                id="password"
                name="password"
                value={SignupForm.password}
                placeholder="Password"
                className="Input"
                onChange={onChange}
                onFocus={() => {
                  onClick();
                  setDisplayRules(true);
                }}
                onBlur={() => setDisplayRules(false)}
              />
              <AiFillEye
                style={{
                  height: "2rem",
                  width: "2rem",
                  marginTop: "0.5rem",
                  color: "rgb(175, 174, 174)",
                  cursor: "pointer",
                }}
                onClick={() => {
                  showPassword ? setShowPassword(false) : setShowPassword(true);
                }}
              />
              {isValid ? (
                <TickIcon
                  style={{
                    height: "2rem",
                    width: "2rem",
                    marginTop: "0.5rem",
                  }}
                />
              ) : null}
            </div>
          </div>
          <div>
            {displayRules ? (
              <>
                <div className="Password-rules">
                  {displayablerule.map((item, index) => {
                    return (
                      <div className="Rule">
                        {" "}
                        <div className="checkbox">
                          {PasswordPolicyState[getKeys[index]] ? (
                            <TiTick className="tick" />
                          ) : (
                            <MdOutlineCancel className="cancel" />
                          )}
                        </div>
                        {item}
                      </div>
                    );
                  })}
                </div>
              </>
            ) : (
              console.log("not going inside that")
            )}
          </div>
          <div>
            {SignupForm.confirmPassword !== "" ? (
              <div
                className="InputLabelCPass"
                style={{
                  color:
                    SignupForm.password === SignupForm.confirmPassword &&
                    SignupForm.confirmPassword !== ""
                      ? "#0CA77D"
                      : "rgb(175, 174, 174)",
                }}
              >
                {translate("confirm_password")}
              </div>
            ) : null}
            <div
              className="InputAndLogoSignup"
              // style={{
              //   border:
              //     LoginError.isEmailError === true
              //       ? "2px solid red"
              //       : LoginError.isEmailError === false
              //       ? "2px solid green"
              //       : "",
              // }}
              style={{
                border: `1px solid ${
                  SignupForm.password === SignupForm.confirmPassword &&
                  SignupForm.confirmPassword !== ""
                    ? "#0CA77D"
                    : "RGB(212, 213, 219)"
                }`,
              }}
            >
              <MdLockOutline
                style={{
                  height: "2rem",
                  width: "2rem",
                  marginTop: "0.5rem",
                  color: "rgb(175, 174, 174)",
                }}
              />
              <input
                type={showPassword ? "text" : "password"}
                id="confirmPassword"
                name="confirmPassword"
                value={SignupForm.confirmPassword}
                placeholder="Confirm Password"
                className="Input"
                onChange={onChange}
                onBlur={() => setDisplayRules(false)}
              />
              <AiFillEye
                style={{
                  height: "2rem",
                  width: "2rem",
                  marginTop: "0.7rem",
                  color: "rgb(175, 174, 174)",
                  cursor: "pointer",
                }}
                onClick={() => {
                  showPassword ? setShowPassword(false) : setShowPassword(true);
                }}
              />
              {SignupForm.password === SignupForm.confirmPassword &&
              SignupForm.confirmPassword !== "" ? (
                <TickIcon
                  style={{
                    height: "2rem",
                    width: "2rem",
                    marginTop: "0.5rem",
                  }}
                />
              ) : null}
            </div>
          </div>
          {SignupError.errorCode && (
            <div className="Error">{translate(SignupError.errorCode)}</div>
          )}
          <div className="PolicyLink">
            <p>
              {" "}
              By clicking{" "}
              <span style={{ fontWeight: "bold" }}>Create my account</span>, you
              accept
              <span style={{ color: "rgb(66, 88, 255)" }}>
                McAfeeâ€™s License <br />
                Agreement
              </span>{" "}
              and
              <span style={{ color: "rgb(66, 88, 255)" }}> Privacy Notice</span>
            </p>
          </div>
          <button
            className={
              SignupForm.email !== "" &&
              SignupForm.password !== "" &&
              SignupForm.confirmPassword !== ""
                ? "SubmitButtonActive"
                : "SubmitButton"
            }
            onClick={onSubmit}
          >
            <div>Create My Account</div>
          </button>
        </>
      </form>
    </div>
  );
};

export default Signup;
